<?php/** * @version		1.0 * @package		Site 7 Dias * @subpackage	mod_s7d_redes * @copyright	Copyright (C) 2015 - 2015 Site 7 Dias - Todos os direitos reservados. */// Acessodefined('_JEXEC') or die;class modS7dRedes{	public static function getItens(&$params)	{		$list = new stdClass();		$style = array(			"default" => (object) array 			(				"Face"	=> '<i class="fa fa-facebook"></i>',				"You" 	=> '<i class="fa fa-youtube"></i>',				"Twi" 	=> '<i class="fa fa-twitter"></i>',				"Gplus"	=> '<i class="fa fa-google-plus"></i>',				"Inst"	=> '<i class="fa fa-instagram"></i>',				"Link" => '<i class="fa fa-linkedin"></i>',				"Whats"	=> '<i class="fa fa-whatsapp"></i>',						),			"square" => (object) array 			(				"Face" 	=> '<i class="fa fa-facebook-square"></i>',				"You" 	=> '<i class="fa fa-youtube-square"></i>',				"Twi" 	=> '<i class="fa fa-twitter-square"></i>',				"Gplus"	=> '<i class="fa fa-google-plus-square"></i>',				"Inst"	=> '<i class="fa fa-instagram"></i>',				"Link" 	=> '<i class="fa fa-linkedin-square"></i>',				"Whats" => '<i class="fa fa-whatsapp"></i>',			)				);		foreach($style as $ind => $cod):			if($ind == $params->get('sStyle')):				$list->facebook = $cod->Face;				$list->youtube = $cod->You;				$list->twitter = $cod->Twi;				$list->google = $cod->Gplus;				$list->instagram = $cod->Inst;				$list->linkedin = $cod->Link;				$list->whatsapp = $cod->Whats;			endif;		endforeach;		echo 		'			<style type="text/css">				.s7dRedes a {font-size:'.$params->get('sSize').'px}			</style>				';		$list->linkFace = $params->get('linkFace');		$list->linkYou 	= $params->get('linkYou');		$list->linkTwi	= $params->get('linkTwi');		$list->linkGplus = $params->get('linkGplus');		$list->linkInst = $params->get('linkInst');		$list->linkLink = $params->get('linkLink');		preg_match("/\((.*?)\)/s",$params->get('linkWhats'), $m); 		$linkT = preg_replace("/\((.*?)\)/s", "", $params->get('linkWhats'));		$list->linkWhats = $params->get('linkWhats') != '' ? '<span class="sPt">('.$m[1].')</span> '.$linkT : null;				return $list;	}}